ALPHA="abcdefghijklmnopqrstuvwxyz"
def NumbLett(W):
    NUM=0
    A=len(W)
    if A>10:
        A=10
    for L in range(1,A):
        num=0
        for N in range(len(ALPHA)):
            if W[L]==ALPHA[N]:
                num=N+1
                break
        NUM+=num*(10**(2*(10-L)))
    NUM2=NUM+(10**(2*(7)))
    return NUM,NUM2

limite=10000

